import { States } from "../components/Panel";

export function getNeighbors(grid: States[][], pos: [number, number]) {
    let [i, j] = pos;

    let neighbors : [number, number][] = [];

    if (i - 1 >= 0 && grid[i - 1][j] !== "black") {
        neighbors.push([i - 1, j]);
    }
    if (j - 1 >= 0 && grid[i][j - 1] !== "black") {
        neighbors.push([i, j - 1]);
    }
    if (i + 1 < grid.length && grid[i + 1][j] !== "black") {
        neighbors.push([i + 1, j]);
    }
    if (j + 1 < grid[0].length && grid[i][j + 1] !== "black") {
        neighbors.push([i, j + 1]);
    }

    return neighbors;
}

export function reconstructPath(grid: States[][], path: any, current: [number, number])  {
    while (path.hasOwnProperty(`${current[0]}-${current[1]}`)) {
        if (grid[current[0]][current[1]] !== "orange") {
            current = path[`${current[0]}-${current[1]}`]
            if (grid[current[0]][current[1]] !== "orange") {
                grid[current[0]][current[1]] = "purple";
            }
        } else {
            break;
        }
    }
}

// User defined class to store elements and their priorities
class QElement {
    element: any;
    priority: number;
    count: number;

    constructor(element : any, priority: number, count: number) {
        this.element = element;
        this.priority = priority;
        this.count = count;
    }
}

// PriorityQueue class
export class PriorityQueue {
    items: any[];

    constructor() {
        this.items = [];
    }

    contains(element: any) {
        return this.items.some((item) => {
            return JSON.stringify(item) === JSON.stringify(element);
        });
    }

    enqueue(element: any, priority: number, count: number) {
        const qElement = new QElement(element, priority, count);
        let contain = false;

        // Iterate through the entire array to add the element at the correct position based on priority
        for (let i = 0; i < this.items.length; i++) {
            if (this.items[i].priority > qElement.priority) {
                this.items.splice(i, 0, qElement);
                contain = true;
                break;
            }
        }

        // If the element has the highest priority, add it to the end of the array
        if (!contain) {
            this.items.push(qElement);
        }
    }

    dequeue() {
        // If the priority queue is empty, return "No elements in Queue"
        if (this.isEmpty()) {
            return undefined;
        }

        // Remove and return the first element from the array, as it has the highest priority
        return this.items.shift().element;
    }

    isEmpty() {
        // Return true if the priority queue is empty
        return this.items.length === 0;
    }
}
