import { useEffect, useState } from "react";

import { Cell, States } from "./Cell"

import "../css/panel.css"

type Rows = {
    rows: number
    toSubmit: number
}

export default function Panel() {
    const [grid, setGrid] = useState<JSX.Element[][]>();
    const [rows, setRows] = useState<Rows>({rows: 50, toSubmit: 50});
    const [width, setWidth] = useState<number>(10);
    const [color] = useState<States>("white")

    const algorithms: string[] = [
        "A*", 
        "Beam Search",
        "Bellman-Ford",
        "Best First Search",
        "BFS", 
        "DFS",
        "Dijkstra",
        "Floyd-Warshall",
        "Greedy Best First Search",
        "Greedy Best Line Search",
        "Iterative Deepening A*",
        "Iterative Deepening DFS",
        "Jump Point Search",
        "Lexicographic BFS",
        "Lifelong Planning A*",
        "Random Search",
        "Theta*",
    ]


    useEffect(() => {
        makeNewGrid(rows.rows, width);
    }, [])

    const makeNewGrid = (rows: number, width: number) => {
        let tempOut : JSX.Element[][] = [];

        for (let j = 0; j < rows; j++) {
            let tempIn : JSX.Element[] = [];
            for (let i = 0; i < rows; i++) {
                tempIn.push(
                    <Cell 
                        row={j} 
                        col={i} 
                        width={width} 
                        state={color} 
                        key={i}
                    />
                );
            }
            tempOut.push(tempIn);
        }

        setGrid([...tempOut])
    }

    const changeRows = (newRows: string) => {
        if (isNaN(Number(newRows))) return;
        setRows({rows: rows.rows, toSubmit: Number(newRows)})
    }

    return(
        <div className="panel">
            <div className="options">
                <div>
                    <span>Rows: </span>
                    <input 
                        value={rows.toSubmit} 
                        onChange={(event) => changeRows(event.target.value)}
                        onKeyDown={(event) => {
                            if (event.key === "Enter") {
                                setRows({rows: rows.toSubmit, toSubmit: rows.toSubmit})
                                makeNewGrid(rows.toSubmit, width)
                            }
                        }}
                    />
                </div>
                <div>
                    <span>Cell Width: </span>
                    <input 
                        value={width} 
                        onChange={(event) => setWidth(Number(event.target.value))}
                        onKeyDown={(event) => {
                            if (event.key === "Enter") {
                                setRows({rows: rows.toSubmit, toSubmit: rows.toSubmit})
                                makeNewGrid(rows.toSubmit, width)
                            }
                        }}
                    />
                </div>
                <div>
                    <span>Algorithm: </span>
                    <select>
                        {algorithms.map((func, index) => {
                            return <option value={func} key={index}>{func}</option>
                        })}
                    </select>
                </div>
                <button>Generate Maze</button>
                <button onClick={() => makeNewGrid(rows.rows, width)}>Clear Grid</button>
                <button>Run</button>
                <button>Stop</button>
            </div>
            <div className="grid" onDragStart={(e)=>e.preventDefault()} onDrop={(e)=>e.preventDefault()}>
                <div style={{ display: 'grid', gridTemplateColumns: `repeat(${rows.rows}, 1fr)` }}>
                    {grid?.map((cells) => {
                        return cells.map((cell, i) => {
                            return (
                                <div key={i}>
                                    {cell}
                                </div>
                            )
                        })
                    })}
                </div>
            </div>
        </div>
    );
}
