import { useEffect, useState } from "react";

import "../css/panel.css"

import bfs from "../algorithms/bfs";

type Rows = {
    rows: number
    toSubmit: number
}

/**
 * Empty Node: white
 * Obstacle Node: black
 * Closed Node: red
 * Open Node: green
 * Start Node: orange
 * End Node: blue
 * Path Node: purple
 */
export type States = "white" | "black" | "red" | "green" | "orange" | "blue" | "purple";

type Interests = {
    start: [number, number]
    end: [number, number]
}

export default function Panel() {
    const [colorGrid, setColorGrid] = useState<States[][]>([]!);
    const [rows, setRows] = useState<Rows>({rows: 25, toSubmit: 25});
    const [width, setWidth] = useState<number>(30);
    const [interestPoints, setInterestPoints] = useState<Interests>({start: [-1, -1], end: [-1, -1]})

    const algorithms: string[] = [
        "A*", 
        "Beam Search",
        "Bellman-Ford",
        "Best First Search",
        "BFS", 
        "DFS",
        "Dijkstra",
        "Floyd-Warshall",
        "Greedy Best First Search",
        "Greedy Best Line Search",
        "Iterative Deepening A*",
        "Iterative Deepening DFS",
        "Jump Point Search",
        "Lexicographic BFS",
        "Lifelong Planning A*",
        "Random Search",
        "Theta*",
    ]


    useEffect(() => {
        makeNewGrid(rows.rows);
    }, [])
    console.log(interestPoints)
    const makeNewGrid = (rows: number) => {
        let tempColorRows : States[][] = [];

        for (let j = 0; j < rows; j++) {
            let tempColorRow : States[] = []; 
            for (let i = 0; i < rows; i++) {
                tempColorRow.push("white");
            }
            tempColorRows.push(tempColorRow);
        }

        setInterestPoints({start: [-1, -1], end: [-1, -1]});
        setColorGrid([...tempColorRows])
    }

    const changeRows = (newRows: string) => {
        if (isNaN(Number(newRows))) return;
        setRows({rows: rows.rows, toSubmit: Number(newRows) > 25 ? 25 : Number(newRows)})
    }

    const cellOnClick = (i: number, j: number, color: States, event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
        const ctrl : boolean = event.nativeEvent.ctrlKey;

        if (event.nativeEvent.button !== 0) {
            return;
        }

        setColorGrid((prev) => {
            const newColorGrid = [...prev];

            let newColor : States = 
                ctrl ? "white" : 
                interestPoints.start[0] === -1 ? "orange" :
                interestPoints.end[0] === -1 ? "blue" :
                "black";

            let newInterest : Interests = {...interestPoints};
            if (newColor === "orange") {
                newInterest.start = [i, j];
            } else if (newColor === "blue") {
                newInterest.end = [i, j];
            } else if (newColor === "white") {
                if (newInterest.start[0] === i && newInterest.start[1] === j) {
                    newInterest.start = [-1, -1];
                }else if (newInterest.end[0] === i && newInterest.end[1] === j) {
                    newInterest.end = [-1, -1];
                }
            }

            setInterestPoints(newInterest);

            newColorGrid[i][j] = newColor;

            return newColorGrid;
        })
    }

    const cellOnMouseOver = (i: number, j: number, color: States, event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
        const ctrl : boolean = event.ctrlKey;

        if (event.buttons !== 1) {
            return;
        }

        setColorGrid((prev) => {
            const newColorGrid = [...prev];

            let newColor : States = 
                ctrl ? "white" : 
                interestPoints.start[0] === -1 ? "orange" :
                interestPoints.end[0] === -1 ? "blue" :
                color !== "orange" && color !== "blue" ? "black" : color;

            let newInterest : Interests = {...interestPoints};
            if (newColor === "orange") {
                newInterest.start = [i, j];
            } else if (newColor === "blue") {
                newInterest.end = [i, j];
            } else if (newColor === "white") {
                if (newInterest.start[0] === i && newInterest.start[1] === j) {
                    newInterest.start = [-1, -1];
                }else if (newInterest.end[0] === i && newInterest.end[1] === j) {
                    newInterest.end = [-1, -1];
                }
            }

            setInterestPoints(newInterest);

            newColorGrid[i][j] = newColor;

            return newColorGrid;
        })
    }

    const draw = (i: number, j: number, color: States, ctrl: boolean, button: boolean, event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
        if (!button) {
            return;
        }

        setColorGrid((prev) => {
            const newColorGrid = [...prev];

            let newColor : States = 
                ctrl ? "white" : 
                interestPoints.start[0] === -1 ? "orange" :
                interestPoints.end[0] === -1 ? "blue" :
                color !== "orange" && color !== "blue" ? "black" : color;

            let newInterest : Interests = {...interestPoints};
            if (newColor === "orange") {
                newInterest.start = [i, j];
            } else if (newColor === "blue") {
                newInterest.end = [i, j];
            } else if (newColor === "white") {
                if (newInterest.start[0] === i && newInterest.start[1] === j) {
                    newInterest.start = [-1, -1];
                }else if (newInterest.end[0] === i && newInterest.end[1] === j) {
                    newInterest.end = [-1, -1];
                }
            }

            setInterestPoints(newInterest);

            newColorGrid[i][j] = newColor;

            return newColorGrid;
        })
    }

    return(
        <div className="panel">
            <div className="options">
                <div>
                    <span>Rows: </span>
                    <input 
                        value={rows.toSubmit} 
                        type="number"
                        onChange={(event) => changeRows(event.target.value)}
                        onKeyDown={(event) => {
                            if (event.key === "Enter") {
                                setRows({rows: rows.toSubmit, toSubmit: rows.toSubmit})
                                makeNewGrid(rows.toSubmit)
                            }
                        }}
                    />
                </div>
                <div>
                    <span>Cell Width: </span>
                    <input 
                        value={width} 
                        type="number"
                        onChange={(event) => setWidth(Number(event.target.value))}
                        onKeyDown={(event) => {
                            if (event.key === "Enter") {
                                setRows({rows: rows.toSubmit, toSubmit: rows.toSubmit})
                                makeNewGrid(rows.toSubmit)
                            }
                        }}
                    />
                </div>
                <div>
                    <span>Algorithm: </span>
                    <select>
                        {algorithms.map((func, index) => {
                            return <option value={func} key={index}>{func}</option>
                        })}
                    </select>
                </div>
                <button>Generate Maze</button>
                <button onClick={() => makeNewGrid(rows.rows)}>Clear Grid</button>
                <button>Run</button>
                <button>Stop</button>
            </div>
            <div className="grid" onDragStart={(e)=>e.preventDefault()} onDrop={(e)=>e.preventDefault()}>
                <div style={{ display: 'grid', gridTemplateColumns: `repeat(${rows.rows}, 1fr)` }}>
                    {colorGrid?.map((colors, i) => {
                        return colors.map((color, j) => {
                            return (
                                <div
                                    onClick={(event) => {
                                        cellOnClick(i,j,color,event);
                                    }}
                                    onMouseOver={(event) => {
                                        cellOnMouseOver(i,j,color,event);
                                    }}
                                    style={{
                                        width: `${width}px`,
                                        height: `${width}px`,
                                        backgroundColor: color,
                                        outline: "solid"
                                    }}
                                    key={`${j}-${i}`}
                                />
                            )
                        })
                    })}
                </div>
            </div>
        </div>
    );
}
