import { useEffect, useState } from "react";

import "../css/panel.css"

type CellProps = {
    row: number
    width: number
    state: States
}

type States = "white" | "black" | "red" | "green" | "orange" | "blue";

type Rows = {
    rows: number
    toSubmit: number
}

const Cell : React.FC<CellProps> = ({row: number, width = 50, state = "white"} : CellProps) => {
    const [color, setColor] = useState<States>(state);
    return(
        <div 
            onClick={(event) => {
                if (state !== "orange" && state !== "blue") {
                    if (event.nativeEvent.button === 0 && event.nativeEvent.ctrlKey === false) {
                        setColor("black")
                    } else if (event.nativeEvent.button === 0 && event.nativeEvent.ctrlKey === true) {
                        setColor("white")
                    }
                }
            }}
            style={{
                width: `${width}px`,
                height: `${width}px`,
                backgroundColor: color,
                outline: "solid",
                margin: "1px",
            }}
        />
    )
}

export default function Panel() {
    const [grid, setGrid] = useState<JSX.Element[][]>();
    const [rows, setRows] = useState<Rows>({rows: 50, toSubmit: 50});
    const [width, setWidth] = useState<number>(10);

    const options: string[] = [
        "A*", "BFS", "DFS"
    ]

    useEffect(() => {
        makeNewGrid(rows.rows, width);
    }, [])

    const makeNewGrid = (rows: number, width: number) => {
        let tempOut : JSX.Element[][] = [];

        for (let j = 0; j < rows; j++) {
            let tempIn : JSX.Element[] = [];
            for (let i = 0; i < rows; i++) {
                tempIn.push(<Cell row={j} width={width} state={"white"} key={i}/>)
            }
            tempOut.push(tempIn);
        }

        setGrid(tempOut)
    }

    const changeRows = (newRows: string) => {
        if (isNaN(Number(newRows))) return;
        setRows({rows: rows.rows, toSubmit: Number(newRows)})
    }

    return(
        <div className="panel">
            <div className="options">
                <div>
                    <span>Rows: </span>
                    <input 
                        value={rows.toSubmit} 
                        onChange={(event) => changeRows(event.target.value)}
                        onKeyDown={(event) => {
                            if (event.key === "Enter") {
                                setRows({rows: rows.toSubmit, toSubmit: rows.toSubmit})
                                makeNewGrid(rows.toSubmit, width)
                            }
                        }}
                    />
                </div>
                <div>
                    <span>Cell Width: </span>
                    <input 
                        value={width} 
                        onChange={(event) => setWidth(Number(event.target.value))}
                        onKeyDown={(event) => {
                            if (event.key === "Enter") {
                                setRows({rows: rows.toSubmit, toSubmit: rows.toSubmit})
                                makeNewGrid(rows.toSubmit, width)
                            }
                        }}
                    />
                </div>
                <div>
                    <span>Algorithm: </span>
                    <select>
                        {options.map((func, index) => {
                            return <option value={func} key={index}>{func}</option>
                        })}
                    </select>
                </div>
            </div>
            <div className="grid">
                <div style={{ display: 'grid', gridTemplateColumns: `repeat(${rows.rows}, 1fr)` }}>
                    {grid?.map((cells) => {
                        return cells.map((cell, i) => {
                            return (
                                <div key={i}>
                                    <Cell 
                                        row={cell.props.row} 
                                        width={cell.props.width}
                                        state={ i === 5 ? "red" : cell.props.state}
                                    />
                                </div>
                            )
                        })
                    })}
                </div>
            </div>
        </div>
    );
}
