import { useEffect, useState } from "react";

import "../css/panel.css"

type CellProps = {
    row: number
    width: number
    state: States
}

type States = "gray" | "red" | "green" | "orange" | "blue";

const Cell : React.FC<CellProps> = ({row: number, width = 50, state = "gray"} : CellProps) => {
    return(
        <div style={{
            width: `${width}px`,
            height: `${width}px`,
            backgroundColor: state,
            margin: "1px",
        }}/>
    )
}

export default function Panel() {
    const [grid, setGrid] = useState<JSX.Element[][]>();
    const [rows, setRows] = useState<number>(71);

    useEffect(() => {
        let tempOut : JSX.Element[][] = [];
        for (let j = 0; j < rows; j++) {
            let tempIn : JSX.Element[] = [];
            for (let i = 0; i < rows; i++) {
                tempIn.push(<Cell row={j} width={10} state={"gray"} key={i}/>)
            }
            tempOut.push(tempIn);
        }
        setGrid(tempOut)
    }, [])

    const makeNewGrid = (rows: number, width: number) => {
        let tempOut : JSX.Element[][] = [];

        for (let j = 0; j < rows; j++) {
            let tempIn : JSX.Element[] = [];
            for (let i = 0; i < rows; i++) {
                tempIn.push(<Cell row={j} width={10} state={"gray"} key={i}/>)
            }
            tempOut.push(tempIn);
        }

        setGrid(tempOut)
    }

    return(
        <div className="panel">
            <div className="options">
                <input value={rows | 0} onChange={(event) => console.log(event)}></input>
            </div>
            <div className="grid">
                <div style={{ display: 'grid', gridTemplateColumns: `repeat(${rows}, 1fr)` }}>
                    {grid?.map((cells) => {
                        return cells.map((cell, i) => {
                            return (
                                <div key={i}>
                                    <Cell 
                                        row={cell.props.row} 
                                        width={cell.props.width}
                                        state={ i === 5 ? "red" : cell.props.state}
                                    />
                                </div>
                            )
                        })
                    })}
                </div>
            </div>
        </div>
    );
}
