import { useEffect, useState } from "react";

type CellProps = {
    row: number
    col: number
    width: number
    state: States
}

export type States = "white" | "black" | "red" | "green" | "orange" | "blue";

export const Cell : React.FC<CellProps> = ({row, col, width = 50, state = "white"} : CellProps) => {
    const [color, setColor] = useState<States>(state);

    useEffect(() => {
        setColor(state)
    },[state])

    return(
        <div 
            onClick={(event) => {
                if (state !== "orange" && state !== "blue") {
                    if (event.nativeEvent.button === 0 && 
                        event.nativeEvent.ctrlKey === false) {
                        setColor("black")
                    } else if (event.nativeEvent.button === 0 && 
                        event.nativeEvent.ctrlKey === true) {
                        setColor("white")
                    }
                }
            }}
            onMouseOver={(event) => {
                if (state !== "orange" && state !== "blue") {
                    if (event.buttons === 1 && event.ctrlKey === false) {
                        setColor("black")
                    } else if (event.buttons === 1 && event.ctrlKey === true) {
                        setColor("white")
                    }
                }
            }}
            style={{
                width: `${width}px`,
                height: `${width}px`,
                backgroundColor: color,
                outline: "solid"
            }}
        />
    )
}
